import { useEffect, useState } from "react";
import "./App.css";
import SensorData from "./Components/SensorData.js";

const serverName = "http://192.168.31.241";

function App() {
  const [sensorData, setSensorData] = useState({
    tableNo: 0,
    zone: 0,
    occupied: 0,
  });

  const [LED, setLED] = useState(false);

  const toggleLED = () => {
    setLED((prevState) => {
      return !prevState;
    });

    fetch(serverName + ":5000/toggle", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        state: LED,
      }),
    });
  };

  useEffect(() => {
    const interval = setInterval(() => {
      fetch(serverName + ":5000/data").then((response) =>
        response.json().then((data) => {
          setSensorData(data);
        })
      );
    }, 750);
    return () => clearInterval(interval);
  });

  return (
    <div className="App">
      <div className="header">Table Occupancy System</div>
      <label className="clabel">Sensor Data</label>
      <div className="sensors">
        <SensorData name="tableNo" data={sensorData.tableNo} />
        <SensorData name="zone" data={sensorData.zone} />
        <SensorData name="occupied" data={sensorData.occupied} />
      </div>
      <label className="clabel">Control Panel</label>
      <div className="control">
        <button onClick={toggleLED}>Toggle LED</button>
      </div>
    </div>
  );
}

export default App;
